INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('3_6_GAS', 3.6, '3.6L V6 24V VVT Engine UPG I @/ESS', 'GASOLINE', 18, 25, 1, 'The 3.6L Pentastar V6 engine delivers excellent performance, reflecting its compact, lightweight design with dual overhead camshafts, high-flow intake and exhaust ports, variable valve timing and electronic throttle control with integrated Speed Control.', 0.00);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('6_4_GAS', 6.4, '6.4L V8 SRT HEMI MDS Engine', 'GASOLINE', 15, 25, 0, 'The 6.4L HEMI SRT V8 engine, teamed with the eight-speed automatic transmission featuring steering wheel-mounted paddle shifters, delivers exceptional performance.', 0.00);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('2_4_GAS_STOP_START', 2.4, '2.4L I4 Zero Evap MultiAir Engine w/ Start / Stop', 'GASOLINE', 21, 29, 1, 'The 2.4L Tigershark MultiAir 2 four-cylinder engine delivers impressive horsepower and torque. It features 16 valves, Variable Valve Timing and a flow-control valve intake manifold as well as Engine Stop/Start (ESS) technology. When the vehicle brakes to a stop, fuel flow is cut and the engine shuts off. When the brake pedal is released, the engine automatically restarts and the nine-speed automatic transmission is engaged.', 0.00);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('2_4_GAS', 2.4, '2.4L I4 Zero Evap M-Air Engine', 'GASOLINE', 22, 31, 0, 'The 2.4L Tigershark MultiAir engine', 0);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('1_3_TURBO', 1.3, '1.3L I4 Turbo MAir DI Engine w/ESS', 'GASOLINE', 22, 27, 1, 'The 1.3L MultiAir Turbo engine with direct Injection Engine Stop/Start Technology when mated to the nine-speed automatic transmission, for efficient performance.', 0.00);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('2_0_TURBO', 2.0, '2.0L I4 DOHC DI Turbo Engine with Start Stop', 'GASOLINE', 22, 24, 1, 'The 2.0L Four-Cylinder DOHC Turbo engine with Engine Stop/Start (ESS) Technology provides plenty of power for on or off-road driving. ESS automatically shuts the engine off at full stops, then re-engages for takeoff. The technology is engineered to help deliver an efficient performance, and can be disabled with the push of a button', 0.00);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('3_0_DIESEL', 3.0, '3.0L V6 Turbo Diesel Engine with Start Stop', 'DIESEL', 22, 32, 1, 'The available 3.0L EcoDiesel V6 engine will offer up to 260 horsepower and 442 lb-ft of torque for impressive performance and outstanding low-end torque', 4500.00);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('2_0_HYBRID', 2.0, '2.0L I4 DOHC DI Turbo PHEV Engine', 'HYBRID', 20, 25, 1, 'The eTorque system adds a 48-volt electric motor to a 2.0-liter turbocharged engine for a total of 295 pound-feet of torque.', 0);
INSERT INTO engines(engine_id, size_in_liters, name, fuel_type, mpg_city, mpg_hwy, has_start_stop, description, price) VALUES('3_6_HYBRID', 3.6, '3.6L V6 24V VVT eTourque Engine', 'HYBRID', 19, 24, 1, 'The eTorque system is designed to function as a replacement to a traditional alternator with its belt-driven motor generator unit. The unit itself is responsible for several functions on the vehicle, including operating the stop-start system that helps conserve fuel economy. A 48-volt battery pack powers the motor generator unit itself, making the eTorque system a system that contributes to better fuel economy, value, performance, and drivability.', 1500.00);
